package chap11.part01.String;

public class MathTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.abs(-53.7745227)); // 절대값
		
		System.out.println(Math.ceil(14.547824)); // 올림
		
		System.out.println(Math.floor(48.55746841)); // 내림
		
		System.out.println(Math.round(8.521423)); // 반올림
		
		System.out.println(Math.max(5, 3)); // 두 수중 큰값 리턴
		
		System.out.println(Math.min(5, 3)); // 두 수중 작은값 리턴
		
		System.out.println(Math.random()); // 0<= i <1 랜덤 숫자
		System.out.println(Math.random()); // 0<= i <1 랜덤 숫자
		
		System.out.println(Math.rint(9.57413684)); // 가장 가까운 정수 리턴
	}

}
